var mapStateToProps = function mapStateToProps(state) {
  return {
    // trainingReducer:state.trainingReducer,
    domainList: state.trainingReducer.domainList,
    URL: state.trainingReducer.URL,
    currentDomain: state.trainingReducer.currentDomain,
    removedDomain: state.trainingReducer.removedDomain
  };
};

var mapDispatchToProps = function mapDispatchToProps(dispatch) {
  return {
    loadUrl: function loadUrl(obj) {
      dispatch({
        type: types.URL,
        payload: obj
      });
    },
    updateUrlElement: function updateUrlElement(URL) {
      dispatch({
        type: types.UPDATE_URL_ELEMENT,
        payload: URL
      });
    },
    createGoal: function createGoal(array) {
      dispatch({
        type: types.CREATE_GOAL,
        payload: array
      });
    },
    revertTrainingReducer: function revertTrainingReducer() {
      dispatch({
        type: types.REVERT_TRAINING_REDUCER,
        payload: []
      });
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps, null, {
  forwardRef: true
})(TrainingModel);

